import React, { Component } from "react";
import '../main/Calculator.css';

import Button from "../components/Button";
import Display from "../components/Display";

const initialState = {
    displayValue: '0',
    clearMemory: false,
    operator: null,
    values: [0, 0],
    current: 0
}

export default class Calculator extends Component {
    state = {...initialState}

    constructor(props) {
        super(props)

        this.clearMemory = this.clearMemory.bind(this);
        this.setOperation = this.setOperation.bind(this);
        this.addDigit = this.addDigit.bind(this);
    }

    clearMemory() {
        this.setState({...initialState});
    }

    setOperation(operator) {
        console.log(operator);
    }

    addDigit(n) {
        if(n === '.' && this.state.displayValue.includes('.')) return

        const clearDisplay = this.state.displayValue === '0' || this.state.clearMemory

        const currentValue = clearDisplay ? '' : this.state.displayValue;

        const displayValue = currentValue + n;

        this.setState( {displayValue, clearDisplay: false} );

        if(n !== '.') {
            const i = this.state.current;
            console.log(i)
            const newValue = parseFloat(displayValue);

            const values = [...this.state.values]
            values[i] = newValue;

            this.setState({values});

            console.log(values);
        }
    }

    render() {
        return (
            <div className="calculator">
                <Display value={this.state.displayValue}/>
                <Button label="AC" click={this.clearMemory} col3/>
                <Button label="/" click={this.setOperation} operator/>
                <Button label="7" click={this.addDigit}/>
                <Button label="8" click={this.addDigit}/>
                <Button label="9" click={this.addDigit}/>
                <Button label="*" click={this.setOperation} operator/>
                <Button label="4" click={this.addDigit}/>
                <Button label="5" click={this.addDigit}/>
                <Button label="6" click={this.addDigit}/>
                <Button label="-" click={this.setOperation} operator/>
                <Button label="1" click={this.addDigit}/>
                <Button label="2" click={this.addDigit}/>
                <Button label="3" click={this.addDigit}/>
                <Button label="+" click={this.setOperation} operator/>
                <Button label="0" click={this.addDigit} col2/>
                <Button label="." click={this.addDigit}/>
                <Button label="=" click={this.setOperation} operator/>
            </div>
        )
    }
}
